  /**
   * Attaches a screenshot to the Playwright test report
   *
   * @param page - Playwright Page instance
   * @param screenshotName - Name for the screenshot in the report
   * @param testInfo - Playwright TestInfo for attaching to report
   */
  static async attachScreenshotToReport(page, screenshotName, testInfo) {
    const screenshot = await page.screenshot();
    await testInfo.attach(screenshotName, {
      body: screenshot,
      contentType: "image/png"
    });
  }

  /**
   * Takes a screenshot and saves it to file
   *
   * @param page - Playwright Page instance
   * @param screenshotName - Name for the screenshot file (without extension)
   * @param testInfo - Optional TestInfo to also attach to report
   * @param subfolder - Optional subfolder within screenshots directory
   * @returns Promise<string> - Path to the saved screenshot
   */
  static async takeScreenshotToFile(page, screenshotName, testInfo, subfolder) {
    const screenshotsBaseDir = path.join(process.cwd(), "screenshots");
    const screenshotsDir = subfolder ? path.join(screenshotsBaseDir, subfolder) : screenshotsBaseDir;
    if (!fs.existsSync(screenshotsDir)) {
      fs.mkdirSync(screenshotsDir, {
        recursive: true
      });
    }
    const screenshotPath = path.join(screenshotsDir, `${screenshotName}.png`);
    await page.screenshot({
      path: screenshotPath,
      fullPage: true
    });

    // Also attach to test report if testInfo is provided
    if (testInfo) {
      const screenshot = fs.readFileSync(screenshotPath);
      await testInfo.attach(screenshotName, {
        body: screenshot,
        contentType: "image/png"
      });
    }
    console.log(`Screenshot saved: ${screenshotName}`);
    return screenshotPath;
  }

  /**
   * Fills dynamic fields that can be either textbox or combobox
   * This method detects the field type and fills accordingly
   * 
   * Supports read-only combobox fields that require typing to filter options
   * Used for address fields like Country/State that toggle between textbox and combobox
   *
   * @param page - Playwright Page instance for navigation
   * @param textboxLocator - Locator for textbox variant of the field
   * @param comboboxLocator - Locator for combobox variant of the field
   * @param fieldName - Name of the field for logging purposes
   * @param value - Value to fill/select (EXACT match required for combobox)
   *
   * @example
   * await Helper.fillDynamicField(
   *   page,
   *   mailingStateTextbox,
   *   mailingStateCombobox,
   *   "Mailing State",
   *   "California"
   * );
   */
  static async fillDynamicField(page, textboxLocator, comboboxLocator, fieldName, value) {
    try {
      // Check if combobox is visible and enabled
      const comboboxVisible = await comboboxLocator.isVisible({
        timeout: 2000
      }).catch(() => false);
      if (comboboxVisible) {
        console.log(`ðŸ”½ ${fieldName} is a combobox, selecting from dropdown...`);
        await comboboxLocator.click({
          timeout: 10000
        });
        await page.waitForTimeout(1000);
        try {
          // The combobox is read-only, so we need to type to filter options
          // Type the value to filter dropdown options
          console.log(`ðŸ“ Typing "${value}" to filter and select exact match...`);
          await comboboxLocator.type(value, {
            delay: 100
          });
          await page.waitForTimeout(1500);

          // IMPORTANT: Look for EXACT match only - using text normalization
          const exactMatchOption = page.locator(`[role="option"]`).filter({
            hasText: new RegExp(`^${value}$`, 'i')
          }).first();
          const exactMatchVisible = await exactMatchOption.isVisible({
            timeout: 5000
          }).catch(() => false);
          if (exactMatchVisible) {
            await exactMatchOption.click({
              timeout: 5000,
              force: true
            });
            console.log(`âœ… ${fieldName} selected with EXACT match:`, value);
            return;
          }

          // If exact match not found, throw error and try fallback
          throw new Error(`Exact match not found for "${value}"`);
        } catch (e) {
          console.log(`â±ï¸ Exact match not found, attempting keyboard navigation for ${fieldName}...`);
          try {
            // Clear previous attempt
            await comboboxLocator.clear({
              timeout: 2000
            }).catch(() => {});
            await page.waitForTimeout(500);

            // Try again with fresh typing
            await comboboxLocator.type(value, {
              delay: 100
            });
            await page.waitForTimeout(1500);

            // Use keyboard to navigate and select
            await page.keyboard.press("ArrowDown");
            await page.waitForTimeout(500);
            await page.keyboard.press("Enter");
            console.log(`âœ… ${fieldName} selected via keyboard navigation:`, value);
          } catch (e2) {
            console.log(`âŒ Failed to select exact match for ${fieldName}:`, value);
            throw new Error(`Could not select exact value "${value}" for field "${fieldName}". Available options may differ.`);
          }
        }
      } else {
        // Use textbox
        console.log(`ðŸ“ ${fieldName} is a textbox, filling value...`);
        await textboxLocator.fill(value, {
          timeout: 10000
        });
        console.log(`âœ… ${fieldName} filled:`, value);
      }
    } catch (error) {
      console.log(`âš ï¸ Error filling ${fieldName}, attempting textbox as fallback:`, error);
      try {
        await textboxLocator.fill(value, {
          timeout: 10000
        });
        console.log(`âœ… ${fieldName} filled via textbox fallback:`, value);
      } catch (fallbackError) {
        console.log(`âŒ Failed to fill ${fieldName}:`, fallbackError);
        throw fallbackError;
      }
    }
  }
  static generateUniqueValue(baseString) {
    const timestamp = Date.now();
    const randomNum = Math.floor(Math.random() * 1000);
    return `${randomNum}${baseString}${timestamp}`;
  }
  static generateUniqueEmail(baseEmail) {
    const timestamp = Date.now();
    const randomNum = Math.floor(Math.random() * 1000);
    return `test+${timestamp}${randomNum}${baseEmail}`;
  }
